import React from "react";
import userPhoto from "../../assets/images/219983.png";
import axios from "axios";
import styles from './Users.module.css';
import {Users} from "./Users";

export class UsersAPIComponent extends React.Component{






    componentDidMount() {
        // axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=5&count=10`)
            .then(result => {
                this.props.setUsers(result.data.items);
                this.props.setTotalUsersCount(result.data.totalCount);
            })
    }

    onPageChanged = (pageNumber) => {
        this.props.setCurrentPage(pageNumber);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`)
            .then(result => {
                this.props.setUsers(result.data.items);
            })
    }


    render(){
        return(
           <Users ob={this.props} onPageChanged={this.onPageChanged} />
        );
    }
}
