import React from "react";
import {Field, reduxForm} from "redux-form";

export const MyPost = (props) => {

    let posts = props.posts.map((post)=><Post key={post.id} text={post.text} likesCount={post.likesCount}/>)

    let newPostElement = React.createRef();

    let addPost = () => {
        props.addPost();
    }

    let onPostChange = () => {
        let text = newPostElement.current.value;
        props.updateNewPostText(text)
    }

    const onSubmit = (formData) => {
        console.log(formData);
    }

    return(

        <div>
            {posts}
            <div>

                <MyPostFormRedux onSubmit={onSubmit}/>

                {/*<textarea onChange={onPostChange}*/}
                {/*          ref={newPostElement}*/}
                {/*          value={props.newPostText}></textarea>*/}

                {/*<button onClick={addPost}>ADD</button>*/}

            </div>
        </div>
    );
}

const MyPostForm = (props) => {
    return(
        <form onSubmit={props.handleSubmit}>
            <Field name={"profileTextarea"} component={"textarea"} placeholder={"Daradi daradi da tam tam Ararat TV cu Joric Cuartanov"}>

            </Field>
            <button>SEND</button>
        </form>
    );
}

const MyPostFormRedux = reduxForm({
    form: 'profileForm'
})(MyPostForm)
