import React, {useState, useEffect, useRef} from "react";

export const Hooks = (props) => {


    const [name, setName] = useState(`Tom`);
    const [age, setAge] = useState(39);


    let onNameChange = (e) => {
        setName(e.target.value);
    }

    let onAgeChange = (e) => {
        setAge(e.target.value);
    }

    React.useEffect(()=>{
        document.title=`Hello ${name}`;
        console.log('useEffect');
    })
    return(


        <>
            <div><h1>Name : {name}</h1></div>
            <div><input type="text" onChange={onNameChange} value={name}/></div>
            <div><input type="text" onChange={onAgeChange}/></div>
        </>
    );
}
