import React from "react";
import {ADD_POST_ACTION_CREATOR, UPDATE_POST_ACTION_CREATOR} from "../../../redux/profile-reducer";
import {StoreContext} from "../../../storeContext";
import {MyPost} from "./MyPost";

export const MyPostContainer = (props) => {


    let addPost = () => {
        debugger
        props.dispatch(ADD_POST_ACTION_CREATOR());
    }

    let onPostChange = (text) => {
        props.dispatch(UPDATE_POST_ACTION_CREATOR(text));
    }


    return(

        <div>
            <StoreContext.Consumer>
                {(store) =>

                    <MyPost state={props.state} posts={props.state.profilePage.profileData}
                            newPostText={props.state.profilePage.newPostText}
                            addPost={addPost}
                            onPostChange={onPostChange}/>
                }
            </StoreContext.Consumer>
        </div>
    );
}
