import {connect} from "react-redux";
import {mapStateToPropsFactory} from "react-redux/es/connect/mapStateToProps";
import {mapDispatchToPropsFactory} from "react-redux/es/connect/mapDispatchToProps";

const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW'


let initialState = {
    usersData: [
        {id:1, followed:false, fullName:'User 1', status:'Status 1', location: {country: 'Moldova', city:'Chisinau'}},
        {id:2, followed:true, fullName:'User 2', status:'Status 2', location: {country: 'Moldova', city:'Chisinau'}},
        {id:3, followed:false, fullName:'User 3', status:'Status 3', location: {country: 'Moldova', city:'Chisinau'}}
    ]

}

export const usersReducer = (state = initialState, action) =>{

    let stateCopy;

    switch (action.type){
        case FOLLOW:
            return {
                ...state,
                usersData: state.usersData.map( user=> {
                    if(user.id ===action.userId){
                        return {...user, followed: true};
                    }
                })
            }
            return stateCopy;
        case UNFOLLOW:
            return {
                ...state,
                usersData: state.usersData.map( user => {
                    if(user.id === action.userId){
                        return {...user, followed: false};
                    }
                })
            }
            return stateCopy;
        default:
            return state;
    }
}

export const FOLLOW_ACTION_CREATOR = (userId) => ({
    type: FOLLOW,
    userId: userId
})

export const UNFOLLOW_ACTION_CREATOR = (userId) => ({
    type: UNFOLLOW,
    userId: userId
})
