import axios from "axios";


let instanceGetUser = axios.create({
    withCredentials: true,
    baseURL: `https://social-network.samuraijs.com/api/1.0/users/?`,
    headers: {
        'API-KEY':'5d793c99-cbef-4f7e-8001-c502c1ec99a5'
    }
});

let instanceDeleteUser = axios.create({
    withCredentials: true,
    baseURL: `https://social-network.samuraijs.com/api/1.0/follow/?`,
    headers: {
        'API-KEY':'5d793c99-cbef-4f7e-8001-c502c1ec99a5'
    }
})

export const userAPI = {
    getUsers(currentPage=1, pageSize=10){
        return instanceGetUser.get( `page=${currentPage}&count=${pageSize}`)
            .then(result => result.data);
    },
    deleteUser(userId=1){
        return instanceDeleteUser.delete(userId)
            .then(result => result.data);
    }
}
