import {MyPostContainer} from "./MyPost/MyPostContainer";
import {ProfileInfo} from "./MyPost/ProfileInfo";
import {ProfileStatus} from "./MyPost/Post/ProfileStatus";
import {updateUserPersonalInfo, updateUserStatus} from "../../redux/profile-reducer";
import {useNavigate} from "react-router-dom";
import {useStore} from "react-redux";
import {useState} from "react";
import {ProfileData} from "./PersonalInformation/ProfileData";
import {ProfileFormData} from "./PersonalInformation/ProfileFormData";

export const Profile = ({profile,status,updateUserStatus,updateUserPersonalInfo}) => {



    const [edit, setEdit] = useState(false);

    const goToEditMode = () => {
        setEdit(true)
    }

    return(

        <>

            <div>
                {profile.aboutMe}
            </div>

            <ProfileInfo profile={profile}
                         status={status}
                         updateUserStatus={updateUserStatus}/>

            <MyPostContainer />

            <div>
                {edit
                    ? <ProfileFormData updateUserPersonalInfo={updateUserPersonalInfo}/>
                    : <ProfileData goToEditMode={goToEditMode}/>}

            </div>
        </>
    );
}
