import React from "react";
import {addPost, updateNewPostText} from "../../../redux/profile-reducer";
import {MyPost} from "./MyPost";
import {connect} from "react-redux";



let mapStateToProps = (state) => {
    return{
        posts : state.profilePage.profileData,
        newPostText: state.profilePage.newPostText
    }
}


let matDispatchToProps = (dispatch) => {
    addPost: (newMessageBody) => {
        this.props.addPost(newMessageBody)
    }
}

export const MyPostContainer = connect(mapStateToProps, {addPost, updateNewPostText})(MyPost);
