import React from "react";

import {ADD_MESSAGE_CREATOR} from "../../redux/dialogs-reducer";
import {UPDATE_MESSAGE_CREATOR} from "../../redux/dialogs-reducer";
import {Dialogs} from "./Dialogs";


export const DialogsContainer = (props) => {




    let addNewMessage = () => {
        props.dispatch(ADD_MESSAGE_CREATOR());
    }

    let onMessageChange = (body) => {
        props.dispatch(UPDATE_MESSAGE_CREATOR(body))
    }

    return(
        <div>
            <Dialogs state={props.state}
                     addNewMessage={addNewMessage}
                     onMessageChange={onMessageChange}/>
        </div>
    );
}
