import axios, {AxiosResponse} from 'axios';




let instance = axios.create({
    withCredentials: true,
    baseURL: `https://social-network.samuraijs.com/api/1.0/`,
    headers: {
        'API-KEY':'d9f5b548-66e1-4d3a-9a83-125429519095'
    }
});



export enum ResultCodesEnum {
    Success = 0,
    Error = 1,
    CaptchaIsRequired = 10
}











export let userAPI = {
    getUsers(currentPage=1, pageSize=10){
        return instance.get( `users?page=${currentPage}&count=${pageSize}`)
            .then(result => result.data);
    },
    unfollow(userId=1){
        return instance.delete(`follow/${userId}`)
            .then(result => result.data);
        debugger
    },
    follow(userId=1){
        return instance.post(`follow/${userId}`)
            .then(result => result.data);
    },
    getProfile(userId: number){
        console.warn("To get profile of user with id "+userId+" please use profileAPI.getProfile() method");
        profileAPI.getProfile(userId);
    }

}






type ProfileResponseType = {
    userId: number
    lookingForAJob: string
    lookingForAJobDescription: string
    fullName: string
    contacts : { github:string, vk: string, facebook: string, instagram: string, twitter: string, website: string, youtube: string, mainLink: string }
    photos: { small: string, large: string }
}


export let profileAPI = {
    getProfile(userId: number){
        return instance.get<ProfileResponseType>(`profile/${userId}`)
            .then(result => result.data)
    },
    getStatus(userId: number){
        return instance.get(`profile/status/${userId}`)
            .then(result => result.data)
    },
    updateStatus(status: string){
        return instance.put('profile/status',{status: status})
            .then(result => result.data)
    }
}






export const captchaAPI = {
    getCaptcha(){
        return instance.get(`security/get-captcha-url`).then(result=>result.data.url)
    }
}





















type MeResponseType = {
    data: { id: number,login: string,email: string }
    messages: Array<string>
    fieldsErrors: Array<string>
    resultCode: ResultCodesEnum
}

type LoginLogoutResponseType = {
    resultCode: number
    messages: Array<string>
    data: {
        userId: number
    }
}


export const authAPI = {
    me() {
        return instance.get<MeResponseType>(`/auth/me`)
            .then(result => result.data)
    },
    login(email: string, password: string, rememberMe=false,captcha: null | string = null) {
        return instance.post<LoginLogoutResponseType>(`/auth/login`, {email, password, rememberMe,captcha})
    },
    logout(){
        return instance.delete<LoginLogoutResponseType>(`auth/login`)
    }
}
