export const Users = (props) => {

    window.userData=props.usersData;

    let setUsers = () => {
        props.setUsers(
            [
                {id:1, photoUrl: 'https://img.pravda.com/images/doc/5/8/58c097f-0.jpg',
                    followed:false, fullName:'User 1', status:'Status 1', location: {country: 'Moldova', city:'Chisinau'}},
                {id:2, photoUrl: 'https://img.pravda.com/images/doc/5/8/58c097f-0.jpg',
                    followed:true, fullName:'User 2', status:'Status 2', location: {country: 'Moldova', city:'Chisinau'}},
                {id:3, photoUrl: 'https://img.pravda.com/images/doc/5/8/58c097f-0.jpg',
                    followed:false, fullName:'User 3', status:'Status 3', location: {country: 'Moldova', city:'Chisinau'}}
            ]
        )
    }

    return ( <div>
        {
            props.usersData.map((user) => <div key={user.id}>
                <div>
                    <img src={user.photoUrl} alt=""/>
                </div>
                <div>
                    {user.followed
                        ? <button onClick={ () => {props.unfollow(user.id)} }>UNFOLLOW</button>
                        : <button onClick={ () => {props.follow(user.id)} }>FOLLOW</button>}
                </div>

                <div>{user.fullName}</div>
                <div>{user.status}</div>

                <div>{user.location.country}</div>
                <div>{user.location.city}</div>

            </div>)
        }
        <div>
            <button onClick={setUsers}>
                Show More
            </button>
        </div>
        </div>);
}
