import React, {useState, useEffect, useRef} from "react";

export const Hooks = (props) => {


    const [count, setCount] = useState(0);
    const latestCount = useRef(count);
    useEffect(()=>{
        console.log(`You clicked me ${latestCount.current} times`)
    },3000)

    return(
        <>
            <h1>Render's count: {latestCount.current}</h1>
            <button type="text" onClick={() => setCount()}>Click me</button>
        </>
    );
}
