import {instance} from "./api";

type ProfileInfoByIdResponseType = {
    userId: number
    lookingForAJob: boolean
    lookingForAJobDescription: string
    fullName: string
    contacts : { github:string | null, vk: string | null, facebook: string | null
        instagram: string | null, twitter: string | null, website: string | null
        youtube: string | null, mainLink: string | null }
    photos: { small: string | null, large: string | null}
}

type ProfilePutStatus = {
    data: any
    messages: Array<string>
    resultCode:number
}


export let profileAPI = {
    getProfile(userId: number) {
        return instance.get<ProfileInfoByIdResponseType>(`profile/${userId}`)
            .then(result => result.data)
    },
    getStatus(userId: number) {
        return instance.get<string>(`profile/status/${userId}`)
            .then(result => result.data)
    },
    updateStatus(status: string) {
        return instance.put<ProfilePutStatus>('profile/status', {status: status})
            .then(result => result.data)
    }
}
