import React, {useState, useEffect, useRef} from "react";

export const Hooks = (props) => {


    const [name, setName] = useState(`Tom`);



    let onChange = (e) => {
        setName(e.target.value);
    }
    return(


        <>
            <h1>Name : {name}</h1>
            <input type="text" onChange={onChange}/>
        </>
    );
}
