import React from "react";

export class ProfileStatus extends React.Component {

    statusInputRef = React.createRef();

    state = {
        editMode: false
    }

    activateEditMode = () => {
        this.setState({
            editMode: true
        })
    }

    deactivateEditeMode = () => {
        this.setState({
          editMode: false
        })
        this.props.updateUserStatus()
    }

    render() {


        return (
            <>
                {!this.state.editMode &&
                    <div>
                        <span onDoubleClick={this.activateEditMode}>{this.props.status}</span>
                    </div>
                }
                {this.state.editMode &&
                    <div>
                        <input ref={this.statusInputRef} autoFocus={true} onBlur={this.deactivateEditeMode} value={this.props.status}/>
                    </div>
                }
            </>
        );
    }
}
