import style from './Dialogs.module.css'
import {NavLink} from "react-router-dom";

const DialogItem = (props) => {
    return(
        <div className={style.dialog}>
            <NavLink to={"/dialogs/" + props.id}>{props.name}</NavLink>
        </div>
    );
}

const MessageItem = (props) => {
    return(
      <div>
          {props.message}
      </div>
    );
}


export const Dialogs = (props) => {


    let messages = props.state.dialogPage.messageData.map((message)=><MessageItem message={message.message}/>)

    return(
        <div className={style.dialogs}>
            <div className={style.dialogsItem}>
                <div className="item">
                    <DialogItem name="Dialog 1" id="1"/>
                </div>

                <div className="item">
                    <DialogItem name="Dialog 2" id="2"/>
                </div>

                <div className="item">
                    <DialogItem name="Dialog 3" id="3"/>
                </div>

                <div className="item">
                    <DialogItem name="Dialog 4" id="4"/>
                </div>

            </div>

            <div>
                {messages}
            </div>

        </div>
    );
}
