                {portionNumber>1 && <button onClick={setPortionNumber(portionNumber-1)}>PREV</button>}

                {portionNumber<portionCount && <button onClick={setPortionNumber(portionNumber+1)}>NEXT</button>}
            </div>

        </div>
    );
}
