import {connect} from "react-redux";
import {Users} from "./Users";
import {FOLLOW_ACTION_CREATOR,UNFOLLOW_ACTION_CREATOR, SET_USERS_ACTION_CREATOR, SET_PAGE_ACTION_CREATOR} from "../../redux/users-reducer";


let mapStateToProps = (state)  => ({
    usersData: state.usersPage.usersData,
    pageSize: state.usersPage.pageSize,
    totalUsersCount: state.usersPage.totalUsersCount,
    currentPage: state.usersPage.currentPage

})

let mapDispatchToProps = (dispatch) => {

    return {
        follow: (userId) => {
            dispatch(FOLLOW_ACTION_CREATOR(userId));
        },
        unfollow: (userId) => {
            debugger
                dispatch(UNFOLLOW_ACTION_CREATOR(userId))
        },
        setUsers: (users) => {
            dispatch(SET_USERS_ACTION_CREATOR(users))
        },

        setCurrentPage: (page) => {
            dispatch(SET_PAGE_ACTION_CREATOR(page))
        }
    }
}

export const UsersContainer = connect(mapStateToProps, mapDispatchToProps)(Users);
