import React from "react";
import {Field, reduxForm} from "redux-form";
import {Input} from "../common/FormsControls/FormsControls";
import {required} from "../../utils/validators/validator";
import {login, logout,getCaptcha} from "../../redux/auth-reducer";
import {connect} from "react-redux";
import {Redirect} from "react-router-dom";
import styles from './../common/FormsControls/FormsControle.module.css'
import {createField} from "../common/FormsControls/FormsControls";

const LoginForm = ({handleSubmit, error}) => {
    return(
        <form onSubmit={handleSubmit}>
            {createField("Email","email",[required],Input,{type: "text"})}
            {createField("Password","password",[required],Input,{type: "password"})}
            {createField(null,  "rememberMe",null,Input,{type: "checkbox"},"remember me")}

            {error && <div className={styles.formSummaryError}>
                {error}
            </div>
            }
            <button>Send</button>
        </form>
    );
}

console.log(getCaptcha())

const LoginReduxForm = reduxForm({
    form: 'loginForm'
})(LoginForm)

const Login = (props) => {

    const onSubmit = (formData) => {
        console.log(formData);
        let {email, password, rememberMe} = formData
        props.login(email,password,rememberMe);
    }
    if(props.isAuth){
        return <Redirect to="/profile"/>
    }

    return (
        <>
            <h1>Login</h1>
            <LoginReduxForm onSubmit={onSubmit}/>
            <img src={getCaptcha()} alt="captch"/>
        </>
    );
}

let mapStateToProps = (state) => ({
    isAuth: state.auth.isAuth
})




export default connect(mapStateToProps,{login,logout,getCaptcha})(Login)
