import React from "react";

const ADD_POST_CREATOR = () =>  {
    type: 'ADD-POST'}
const UPDATE_POST_CREATOR={ type: 'UPDATE-POST', text: ''}
const ADD_MESSAGE_CREATOR = {type: 'ADD-MESSAGE'}
const UPDATE_MESSAGE_CREATOR = {type: 'UPDATE-MESSAGE'}



export const MyPost = (props) => {

    let posts = props.state.profilePage.profileData.map((post)=><Post text={post.text} likesCount={post.likesCount}/>)

    let newPostElement = React.createRef();

    let addPost = () => {
        debugger
        props.dispatch(ADD_POST_CREATOR);
    }

    let onPostChange = () => {
        let text = newPostElement.current.value;
        props.dispatch({type: 'UPDATE-POST', text: text});
    }
    return(

        <div>
            {posts}
            <div>

                <textarea onChange={onPostChange}
                          ref={newPostElement}
                          value={props.state.profilePage.newPostText}></textarea>

                <button onClick={addPost}>ADD</button>

            </div>
        </div>
    );
}
