import {authAPI,captchaAPI} from "../api/api.js";
import {stopSubmit} from "redux-form";

const SET_USER_DATA='auth/SET_USER_DATA';
const SET_CAPTCHA_IMAGE = 'SET_CAPTCHA_IMAGE';

let initialState = {
    userId: null,
    email: null,
    login: null,
    isAuth: false,
    captchaImg: null
}

export const authReducer = (state=initialState, action) => {
    switch (action.type){
        case SET_USER_DATA: {
            return {
                ...state,
                ...action.payload,
            }
        }
        case SET_CAPTCHA_IMAGE: {
            return {
                ...state,
                captchaImg: action.captchaImg
            }
        }
        default:
            return {
                ...state
            }
    }
}


export const setAuthUserData = (userId, login, email, isAuth) => ({
    type:SET_USER_DATA,
    payload: {userId, login, email, isAuth}
})

export const getAuthUserData = () => async (dispatch) => {
    let data = await authAPI.me();

            if(data.resultCode === 0) {
                let {id,login, email} = data.data;
                dispatch(setAuthUserData(id,login,email,true));
            }
}

export const login = (email, password, rememberMe) => async (dispatch) => {
    let result = await authAPI.login(email, password, rememberMe);
            if(result.data.resultCode === 0){
                dispatch(getAuthUserData())
            } else {
                dispatch(stopSubmit("loginForm",{_error : result.data.messages}))
            }
}

export const logout = () => async (dispatch) => {
    let result = await authAPI.logout()
    debugger
